.w-clock-wrapper {
  height: 100%;
  padding: 0 24px;

}

.w-clock {
  width: 200px;
  height: 200px;
  border-radius: 50%;
  cursor: pointer;
  padding: 24px;
  background: #ededed;
}
.w-clock-container {
  width: 100%;
  height: 100%;
  position: relative;
  display: block;
}
.w-clock-center {
  height: 6px;
  width: 6px;
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  margin: auto;
  border-radius: 50%;
}
.w-pointer {
  box-shadow: none;
  width: 1px;
  height: 50%;
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  margin: 0 auto;
  padding: 0;
  transform-origin: top center;
  transition: transform 200ms;
  z-index: 0;
  pointer-events: none;
}
.w-clock-step {
  display: block;
  position: absolute;
  transform: translate(-50%, -50%);
  transition: transform 200ms;
}
.w-clock-step .mat-mini-fab {
  box-shadow: none;
  background-color: transparent;
}

.w-clock-selected {
  position: absolute;
  bottom: -19px;
  left: -19px;
  min-width: 0;
  min-height: 0;
  pointer-events: none;
  box-shadow: none;
  cursor: none;
}

